uint64 result;

int power(int, int);
template<class T> string stringify(T);

void process(int left, int right, int length, int * found, int * suffix)
{
	if (length == 0 || (!left && !right))
		return;

	bool done = true;
	for (int i = 1; i < 16; i++)
		if (!found[i]) {
			done = false;
			break;
		}

	if (done) return;

	int value = power(10, length - 1);
	int low = left / value, high = right / value;
	int temp = value * low;
	for (int i = low; i <= high; i++) {
		int a = max(temp, left) - temp;
		int b = min(temp + value - 1, right) - temp;
		int len = b - a + 1;
		temp += value;

		int _found[16], _suffix[16];
		memset(_found, 0, sizeof _found);
		memset(_suffix, 0, sizeof _suffix);
		_found[0] = _suffix[0] = 1;

		for (int d = 0; d < 16; d++) {
			if (!suffix[d]) continue;
			int val = (d ^ i);

			if (!found[val]) 
				result = (result + val * 1ULL * len) % MOD;

			_found[val] = _suffix[val] = 1;
		}

		for (int i = 1; i < 16; i++)
			if (found[i])
				_found[i] = 1;

		process(a, b, length - 1, _found, _suffix);
	}
}

uint64 solve(int left, int right)
{
	if (left > right)
		return 0;
	int length = stringify(right).size();
	result = 0;
	int found[16], suffix[16];
	memset(found, 0, sizeof found);
	memset(suffix, 0, sizeof suffix);
	found[0] = suffix[0] = 1;
	process(left, right, length, found, suffix);
	return result;
}

int main()
{
	ios_base::sync_with_stdio(0);
	int tests; cin >> tests;
	int left, right;
	for (int t = 0; t < tests; t++) {
		cin >> left >> right;
		uint64 ans;
		if (right - left + 1 < PERIOD)
			ans = solve(left, right);
		else {
			int low = (left - 1) / PERIOD + 1;
			int high = right / PERIOD;
			ans = (memo[high] + MOD - memo[low]) % MOD;
			ans = (ans + solve(left, PERIOD * low)) % MOD;
			ans = (ans + solve(high * PERIOD + 1, right)) % MOD;
		}
		cout << ans << endl;
	}
}

template<class T>
string stringify(T object)
{
	stringstream ss;
	string ans;
	ss << object;
	ss >> ans;
	return ans;
}

int power(int a, int b)
{
	if (!b)
		return 1;
	int ans = power(a, b >> 1);
	ans *= ans;
	if (b & 1)
		ans *= a;
	return ans;
}